import { useState, useCallback } from 'react';
import { Upload, Loader } from 'lucide-react';
import './ImageSegmentation.css';

function ImageSegmentation() {
  const [originalImage, setOriginalImage] = useState(null);
  const [segmentedImage, setSegmentedImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleImageUpload = useCallback(async (file) => {
    if (!file) return;

    try {
      setLoading(true);
      setError(null);

      const formData = new FormData();
      formData.append('image', file);

      const response = await fetch('http://localhost:5000/segment', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Failed to process image');
      }

      const data = await response.json();

      setOriginalImage(URL.createObjectURL(file));
      setSegmentedImage(`data:image/png;base64,${data.image}`);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, []);

  const onDrop = useCallback((e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file?.type.startsWith('image/')) {
      handleImageUpload(file);
    }
  }, [handleImageUpload]);

  const onDragOver = useCallback((e) => {
    e.preventDefault();
  }, []);

  return (
    <div className="container">
      <center><h1 className="title">Beautify Me</h1></center>

      <div 
        className="upload-section"
        onDrop={onDrop}
        onDragOver={onDragOver}
      >
        <input
          type="file"
          id="imageInput"
          accept="image/*"
          className="hidden"
          onChange={(e) => handleImageUpload(e.target.files[0])}
        />
        <label htmlFor="imageInput">
          <Upload className="upload-icon" />
          <div className="upload-text">Click to upload</div>
          <div className="upload-subtext">or drag and drop image here</div>
        </label>
      </div>

      {loading && (
        <div className="loading">
          <Loader className="upload-icon" style={{ animation: 'spin 1s linear infinite' }} />
          <span>Processing image...</span>
        </div>
      )}

      {error && (
        <div className="error">
          {error}
        </div>
      )}

      {(originalImage || segmentedImage) && (
        <div className="results-grid">
          <div className="image-container">
            <h3 className="image-title">Original Image</h3>
            <div className="image-wrapper">
              {originalImage && (
                <img
                  src={originalImage}
                  alt="Original"
                  className="image"
                />
              )}
            </div>
          </div>

          <div className="image-container">
            <h3 className="image-title">Segmented Image</h3>
            <div className="image-wrapper">
              {segmentedImage && (
                <img
                  src={segmentedImage}
                  alt="Segmented"
                  className="image"
                />
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ImageSegmentation;
